import React, { useState } from "react";
import Layout from "../components/Layout";
import { Document, Page } from "react-pdf";

const MyImage = () => {
  return (
    <img
      src="/images/a.jpg"
      alt="mypic"
      height="auto"
      width="100px"
      style={{ borderRadius: "50%" }}
    />
  );
};

const Resume = () => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div>
      {console.log("whatsup")}
      <Document
        file="../public/files/Resume.pdf"
        onLoadSuccess={onDocumentLoadSuccess}
      >
        <Page pageNumber={pageNumber} />
      </Document>
      <p>
        Page {pageNumber} of {numPages}
      </p>
    </div>
  );
};

const handleClick = () => {
  console.log("resume button");
  return <Resume />;
};

const Index = () => (
  <Layout>
    <div className="intro">
      <div className="introduction">
        <MyImage className="myImage" />
        <p>
          Hello, there <br />
          <span style={{ fontSize: "large" }}> I am </span>
          <span style={{ fontSize: "40px" }}> Pranesh Shrestha. </span>
        </p>
        <p>
          I am currently Computer Engineering Student.
          <br /> Welcome to my Portfolio Website.
        </p>
        <div className="resume">
          <button className="resumebtn" onClick={handleClick}>
            Resume
          </button>
        </div>
      </div>
    </div>
  </Layout>
);

export default Index;
