import React, { Component } from "react";
import Layout from "../components/Layout";
import { Document, Page } from "react-pdf";

const MyImage = () => {
  return (
    <img
      src="/images/a.jpg"
      alt="mypic"
      height="auto"
      width="100px"
      style={{ borderRadius: "50%" }}
    />
  );
};

class Index extends Component {
  state = {
    clicked: false,
  };

  handleClick = () => {
    const state = this.state.clicked == false ? true : false;
    console.log(state);
    this.setState({ clicked: state });
  };

  render() {
    return (
      <Layout>
        <style jsx>
          {`
            .popup {
              position: fixed;
              width: 100%;
              height: 100%;
              top: 0;
              left: 0;
              right: 0;
              bottom: 0;
              margin: auto;
              background-color: rgba(0, 0, 0, 0.5);
            }
            .popup_inner {
              position: absolute;
              left: 25%;
              right: 25%;
              top: 25%;
              bottom: 25%;
              margin: auto;
              border-radius: 20px;
              background: white;
            }
            .closebtn {
              width: 10vw;
            }
          `}
        </style>
        <div className="intro">
          <div className="introduction">
            <MyImage className="myImage" />
            <p>
              Hello, there <br />
              <span style={{ fontSize: "large" }}> I am </span>
              <span style={{ fontSize: "40px" }}> Pranesh Shrestha. </span>
            </p>
            <p>
              I am currently Computer Engineering Student.
              <br /> Welcome to my Portfolio Website.
            </p>
            <div className="resume">
              <button className="resumebtn" onClick={this.handleClick}>
                Resume
              </button>
            </div>
          </div>
        </div>
        {this.state.clicked ? (
          <div className="popup">
            <div style={{ marginLeft: "15vw" }}>
              <Document file="/files/Resume.pdf">
                <Page pageNumber={1} />
              </Document>
              <button className="closebtn" onClick={this.handleClick}>
                Close
              </button>
            </div>
          </div>
        ) : null}
      </Layout>
    );
  }
}

export default Index;
