import React from "react";
import Layout from "../components/Layout";
import { Page, Text, View, Document, StyleSheet } from "@react-pdf/renderer";

const styles = StyleSheet.create({
  page: {
    flexDirection: "row",
    backgroundColor: "#E4E4E4",
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,
  },
});

const MyImage = () => {
  return (
    <img
      src="/images/a.jpg"
      alt="mypic"
      height="auto"
      width="100px"
      style={{ borderRadius: "50%" }}
    />
  );
};

const Resume = () => (
  <Document file="../public/files/Resume.pdf">
    <Page size="A4" style={styles.page}>
      <View style={styles.section}>
        <Text>Section #1</Text>
      </View>
    </Page>
  </Document>
);

const handleClick = () => <Resume />;

const Index = () => (
  <Layout>
    <div className="intro">
      <div className="introduction">
        <MyImage className="myImage" />
        <p>
          Hello, there <br />
          <span style={{ fontSize: "large" }}> I am </span>
          <span style={{ fontSize: "40px" }}> Pranesh Shrestha. </span>
        </p>
        <p>
          I am currently Computer Engineering Student.
          <br /> Welcome to my Portfolio Website.
        </p>
        <div className="resume">
          <a href="/resume">
            <button className="resumebtn" onClick={handleClick}>
              Resume
            </button>
          </a>
        </div>
      </div>
    </div>
  </Layout>
);

export default Index;
