import React, { Component } from "react";

const AfterSubmit = () => (
  <div className="contactdiv" id="aftersubmit" style={{ display: "none" }}>
    <div className="title">
      <span className="topic3"> Contact </span>
    </div>
    <div className="title">
      <span className="topic3">
        {" "}
        Thanks for Submitting the form.
        <br />{" "}
      </span>
      <span className="topic3"> I will get in touch. </span>
    </div>
  </div>
);

const ContactForm = () => (
  <div style={{ padding: "10vh 0" }}>
    <div className="contactdiv" id="formsubmit">
      <div className="title">
        <span className="topic3"> Contact </span>
      </div>
      <form method="POST" id="contactform">
        <div className="name">
          <label htmlFor="firstname">
            firstname
            <input label="firstname" id="firstname" required />
          </label>
          <label htmlFor="lastname">
            Lastname
            <input label="lastname" id="lastname" required />
          </label>
        </div>
        <label htmlFor="email">
          Email
          <input label="email" id="email" required />
        </label>
        <label htmlFor="phone">
          Phone
          <input label="contact no" id="phone" required />
        </label>

        <div className="message">
          <label htmlFor="msg">
            Message
            <input id="msg" label="message" required />
          </label>
        </div>
        <input type="submit" value="Submit" />
      </form>
    </div>
  </div>
);

class Form extends Component {
  state = {
    values: { firstname: "", lastname: "", email: "", phone: "", message: "" },
    submitted: false,
  };

  handleChange = (event) => {
    this.setState({ value: event.target.value });
  };

  handleSubmit = (event) => {
    this.setState({ submitted: true });
    event.preventDefault();
  };

  render() {
    const isSubmitted = this.state.submitted;
    let elements;
    if (isSubmitted) {
      elements = <ContactForm />;
    } else {
      elements = <AfterSubmit />;
    }

    return <div>{elements}</div>;
  }
}

export default Form;

handleChange = (event) => {
  this.setState({ value: event.target });
};
