import React, { Component } from "react";
import "../public/css/styles.css";

const AfterSubmit = () => (
  <div className="contactdiv" id="aftersubmit">
    <div className="title">
      <span className="topic3"> Contact </span>
    </div>
    <div className="title">
      <span className="topic3">
        Thanks for Submitting the form.
        <br />
      </span>
      <span className="topic3"> I will get in touch. </span>
    </div>
  </div>
);

class Form extends Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    message: "",
    submitted: false,
  };

  handleChange = (event) => {
    let tempvar = event.target.name;
    let tempvalue = event.target.value;
    this.setState({ [tempvar]: tempvalue });
  };

  handleSubmit = (event) => {
    this.setState({ submitted: true });
    event.preventDefault();
  };

  render() {
    const isSubmitted = this.state.submitted;
    let elements;
    if (!isSubmitted) {
      elements = (
        <div style={{ padding: "10vh 0 0 0" }}>
          <div className="contactdiv" id="formsubmit">
            <div className="title">
              <span className="topic3"> Contact </span>
            </div>
            <form onSubmit={this.handleSubmit}>
              <div style={{ display: "flex", flexFlow: "row nowrap" }}>
                <div>
                  <label htmlFor="firstname">firstname </label> <br />
                  <input
                    name="firstname"
                    id="firstname"
                    onChange={this.handleChange}
                    required
                  />
                </div>
                <div style={{ marginLeft: "5vw" }}>
                  <label htmlFor="lastname">Lastname </label> <br />
                  <input
                    name="lastname"
                    id="lastname"
                    onChange={this.handleChange}
                    required
                  />
                </div>
              </div>
              <label htmlFor="email">
                Email
                <input
                  name="email"
                  id="email"
                  onChange={this.handleChange}
                  required
                />
              </label>
              <label htmlFor="phone">
                Phone
                <input
                  name="phone"
                  id="phone"
                  onChange={this.handleChange}
                  required
                />
              </label>

              <div className="message">
                <label htmlFor="msg">
                  Message
                  <input
                    id="msg"
                    name="message"
                    onChange={this.handleChange}
                    required
                  />
                </label>
              </div>
              <input type="submit" value="Submit" />
            </form>
          </div>
        </div>
      );
    } else {
      elements = <AfterSubmit />;
    }

    return <div>{elements}</div>;
  }
}

export default Form;
