import React, { Component } from "react";
import "../public/css/styles.css";

const AfterSubmit = () => (
  <div className="contactdiv" id="aftersubmit">
    <div className="title">
      <span className="topic3"> Contact </span>
    </div>
    <div className="title">
      <span className="topic3">
        Thanks for Submitting the form.
        <br />
      </span>
      <span className="topic3"> I will get in touch. </span>
    </div>
  </div>
);

class Form extends Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    message: "",
    submitted: false,
  };

  handleChange = (event) => {
    let tempvar = event.target.name;
    let tempvalue = event.target.value;
    this.setState({ [tempvar]: tempvalue });
  };

  handleSubmit = (event) => {
    this.setState({ submitted: true });
    event.preventDefault();
  };

  render() {
    const isSubmitted = this.state.submitted;
    let elements;
    if (!isSubmitted) {
      elements = (
        <form onSubmit={this.handleSubmit}>
          <ul class="form-style-1">
            <li>
              <label>
                Full Name <span class="required">*</span>
              </label>
              <input
                type="text"
                name="firstname"
                className="field-divided"
                placeholder="First"
              />{" "}
              <input
                type="text"
                name="lastname"
                className="field-divided"
                placeholder="Last"
              />
            </li>
            <li>
              <label>
                Email <span className="required">*</span>
              </label>
              <input type="email" name="email" className="field-long" />
            </li>
            <li>
              <label>Subject</label>
              <select name="field4" class="field-select">
                <option value="Advertise">Advertise</option>
                <option value="Partnership">Partnership</option>
                <option value="General Question">General</option>
              </select>
            </li>
            <li>
              <label>
                Your Message <span class="required">*</span>
              </label>
              <textarea
                name="field5"
                id="field5"
                class="field-long field-textarea"
              ></textarea>
            </li>
            <li>
              <input type="submit" value="Submit" />
            </li>
          </ul>
        </form>
      );
    } else {
      elements = <AfterSubmit />;
    }

    return <div>{elements}</div>;
  }
}

export default Form;
