import React, { Component } from "react";

const AfterSubmit = () => (
  <div className="contactdiv" id="aftersubmit">
    <div className="title">
      <span className="topic3"> Contact </span>
    </div>
    <div className="title">
      <span className="topic3">
        {" "}
        Thanks for Submitting the form.
        <br />{" "}
      </span>
      <span className="topic3"> I will get in touch. </span>
    </div>
  </div>
);

const ContactForm = (props) => (
  <div style={{ padding: "10vh 0" }}>
    {console.log(props)}
    <div className="contactdiv" id="formsubmit">
      <div className="title">
        <span className="topic3"> Contact </span>
      </div>
      <form method="POST" id="contactform">
        <div className="name">
          <label htmlFor="firstname">
            firstname
            <input
              name="firstname"
              id="firstname"
              onChange={props.handleChange}
              required
            />
          </label>
          <label htmlFor="lastname">
            Lastname
            <input
              name="lastname"
              id="lastname"
              onChange={props.handleChange}
              required
            />
          </label>
        </div>
        <label htmlFor="email">
          Email
          <input
            name="email"
            id="email"
            onChange={props.handleChange}
            required
          />
        </label>
        <label htmlFor="phone">
          Phone
          <input
            name="phone"
            id="phone"
            onChange={props.handleChange}
            required
          />
        </label>

        <div className="message">
          <label htmlFor="msg">
            Message
            <input
              id="msg"
              name="message"
              onChange={props.handleChange}
              required
            />
          </label>
        </div>
        <input type="submit" value="Submit" onSubmit={props.handleSubmit} />
      </form>
    </div>
  </div>
);

class Form extends Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    message: "",
    submitted: false,
  };

  handleChange = (event) => {
    let tempvar = event.target.name;
    let tempvalue = event.target.value;
    this.setState({ [tempvar]: tempvalue });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({ submitted: true });
  };

  render() {
    const isSubmitted = this.state.submitted;
    let elements;
    if (!isSubmitted) {
      elements = <ContactForm onChange={this.handleChange} />;
    } else {
      elements = <AfterSubmit />;
    }

    return <div>{elements}</div>;
  }
}

export default Form;
